// Enhanced scroll progress with color transitions and header effects
function updateScrollProgress() {
    const progressBar = document.querySelector('.progress-bar');
    if (progressBar) {
        const winScroll = document.body.scrollTop || document.documentElement.scrollTop;
        const height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
        const scrolled = (winScroll / height) * 100;
        
        progressBar.style.width = scrolled + '%';
        
        // Color transition based on scroll position
        const hue = Math.min(scrolled * 2, 180);
        progressBar.style.background = `linear-gradient(90deg, hsl(${hue}, 70%, 50%), hsl(${hue + 20}, 70%, 60%))`;
    }
}

// Enhanced smooth scrolling with dynamic easing
function smoothScrollTo(targetId) {
    const target = document.querySelector(targetId);
    if (target) {
        const targetPosition = target.offsetTop - 80;
        const startPosition = window.pageYOffset;
        const distance = targetPosition - startPosition;
        const duration = Math.min(Math.abs(distance) / 2, 1200); // Dynamic duration
        let start = null;

        function step(timestamp) {
            if (!start) start = timestamp;
            const progress = timestamp - start;
            const progressPercentage = Math.min(progress / duration, 1);
            
            // Dynamic easing based on distance
            const easing = distance > 1000 
                ? easeInOutQuart(progressPercentage)
                : easeInOutCubic(progressPercentage);
            
            window.scrollTo(0, startPosition + distance * easing);
            
            if (progress < duration) {
                requestAnimationFrame(step);
            }
        }
        
        requestAnimationFrame(step);
    }
}

// Easing functions
function easeInOutCubic(t) {
    return t < 0.5 ? 4 * t * t * t : 1 - Math.pow(-2 * t + 2, 3) / 2;
}

function easeInOutQuart(t) {
    return t < 0.5 ? 8 * t * t * t * t : 1 - Math.pow(-2 * t + 2, 4) / 2;
}

// Performance optimization for animations
const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)');
if (prefersReducedMotion.matches) {
    // Disable complex animations for users who prefer reduced motion
    document.documentElement.style.setProperty('--transition-duration', '0.1s');
}

// Enhanced Performance Monitoring
class PerformanceMonitor {
    constructor() {
        this.metrics = {
            loadTime: 0,
            firstPaint: 0,
            domContentLoaded: 0,
            scrollEvents: 0
        };
        
        this.trackPerformance();
    }
    
    trackPerformance() {
        // Track load time
        window.addEventListener('load', () => {
            this.metrics.loadTime = performance.now();
            this.reportMetrics();
        });
        
        // Track First Paint
        if ('PerformanceObserver' in window) {
            const observer = new PerformanceObserver((list) => {
                for (const entry of list.getEntries()) {
                    if (entry.name === 'first-paint') {
                        this.metrics.firstPaint = entry.startTime;
                    }
                }
            });
            observer.observe({ entryTypes: ['paint'] });
        }
        
        // Track DOM Content Loaded
        document.addEventListener('DOMContentLoaded', () => {
            this.metrics.domContentLoaded = performance.now();
        });
        
        // Throttled scroll tracking
        let scrollCount = 0;
        const throttledScroll = this.throttle(() => {
            scrollCount++;
            this.metrics.scrollEvents = scrollCount;
        }, 100);
        
        window.addEventListener('scroll', throttledScroll);
    }
    
    throttle(func, delay) {
        let timeoutId;
        let lastExecTime = 0;
        return function (...args) {
            const currentTime = Date.now();
            
            if (currentTime - lastExecTime > delay) {
                func.apply(this, args);
                lastExecTime = currentTime;
            } else {
                clearTimeout(timeoutId);
                timeoutId = setTimeout(() => {
                    func.apply(this, args);
                    lastExecTime = Date.now();
                }, delay - (currentTime - lastExecTime));
            }
        };
    }
    
    reportMetrics() {
        if (process.env.NODE_ENV === 'development') {
            console.log('Performance Metrics:', this.metrics);
        }
    }
}

// Enhanced Mobile Navigation
class MobileNavigation {
    constructor() {
        this.menuToggle = document.getElementById('mobileMenuToggle');
        this.mobileNav = document.getElementById('mobileNav');
        this.mobileOverlay = document.getElementById('mobileOverlay');
        this.mobileLinks = document.querySelectorAll('.mobile-nav-link');
        this.isOpen = false;
        
        this.init();
    }
    
    init() {
        if (!this.menuToggle || !this.mobileNav) return;
        
        this.bindEvents();
    }
    
    bindEvents() {
        // Toggle menu
        this.menuToggle.addEventListener('click', () => {
            this.toggleMenu();
        });
        
        // Close on overlay click
        this.mobileOverlay.addEventListener('click', () => {
            this.closeMenu();
        });
        
        // Close on link click
        this.mobileLinks.forEach(link => {
            link.addEventListener('click', () => {
                this.closeMenu();
            });
        });
        
        // Close on escape key
        document.addEventListener('keydown', (e) => {
            if (e.key === 'Escape' && this.isOpen) {
                this.closeMenu();
            }
        });
        
        // Handle resize
        window.addEventListener('resize', () => {
            if (window.innerWidth > 640 && this.isOpen) {
                this.closeMenu();
            }
        });
    }
    
    toggleMenu() {
        if (this.isOpen) {
            this.closeMenu();
        } else {
            this.openMenu();
        }
    }
    
    openMenu() {
        this.isOpen = true;
        this.menuToggle.classList.add('active');
        this.mobileNav.classList.add('active');
        this.mobileOverlay.classList.add('active');
        document.body.style.overflow = 'hidden';
        
        // Focus management
        this.mobileLinks[0]?.focus();
    }
    
    closeMenu() {
        this.isOpen = false;
        this.menuToggle.classList.remove('active');
        this.mobileNav.classList.remove('active');
        this.mobileOverlay.classList.remove('active');
        document.body.style.overflow = '';
    }
}

// Enhanced Progress Bar
class ProgressIndicator {
    constructor() {
        this.progressBar = document.getElementById('progressBar');
        this.init();
    }
    
    init() {
        if (!this.progressBar) {
            this.createProgressBar();
        }
        
        this.updateProgress();
        window.addEventListener('scroll', () => this.updateProgress());
    }
    
    createProgressBar() {
        const container = document.createElement('div');
        container.className = 'progress-container';
        
        const bar = document.createElement('div');
        bar.className = 'progress-bar';
        bar.id = 'progressBar';
        
        container.appendChild(bar);
        document.body.appendChild(container);
        
        this.progressBar = bar;
    }
    
    updateProgress() {
        const winScroll = document.body.scrollTop || document.documentElement.scrollTop;
        const height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
        const scrolled = (winScroll / height) * 100;
        
        if (this.progressBar) {
            this.progressBar.style.width = scrolled + '%';
        }
    }
}

